import React, { useState } from "react";
import Navbar from "../Base/Navbar";
import Header from "../Base/Header";
import { Link } from "react-router-dom";

/*
    Side hvor man får en id andre kan joine med
    kan legge til spørsmål selv
    kan starte spillet når som helst
    har count på antall spørsmål
*/

/*
    TODO - later
    fikse regex lengde på input
*/
const Host = () => {

    const [room, setRoom] = useState([]);

    function createRoom() {
        // her setter vi inn det nye rommet slik at det ikke blir brukt igjen
        const roomID = generateRoomNumber();
        setRoom(room.push());

        {/* Her må det opprettes ny JSON object med nummer som id og resten av spm nedover */}
        console.log("Creating room");
    }

    function generateRoomNumber() {
        console.log("Generating room number ...");

        let number = Math.floor(Math.random() * 9000 + 1000);
        let validNumber = false;

        while(!validNumber) {

            validNumber = true;

            room.forEach(num => {
                if(num === number) {
                    validNumber = false;
                }
            });

            if(!validNumber) {
                number++;
            }

        }

        console.log(number);
        return number;

    }

    return(
        <>
            <Header />

            {/* Form box */}
            <div className="mt-64 justify-center items-center flex">

                <div className="h-64 w-80 bg-violet-500 rounded shadow-xl justify-center items-center flex">
                    <div className="flex-col">
                        <h1 className="justify-center items-center flex font-bold text-white">Navn</h1>
                        <input id="hostName" name="id" className=" h-10 w-64 rounded shadow-md m-1" />

                        <Link to="/howto">    
                            <button onClick={() => createRoom()} id="hostBtn" className="m-1 h-10 w-64 bg-blue-500 rounded shadow-md justify-center items-center flex hover:bg-blue-800">
                                <p>Host</p>
                            </button>
                        </Link>

                    </div>
                </div>
            </div>

            <Navbar />
        </>
    );
}

export default Host;
