import React, { useState } from "react";
import Navbar from "../Base/Navbar";
import Header from "../Base/Header";
import Rooms from "../storage/rooms.json";
import HostGame from "../Components/HostGame";
import CreateRoom from "../Components/CreateRoom";

/*
    TODO - later
    fikse regex lengde på input
*/
const Host = (props) => {

    const [roomID, setRoomID] = useState(0);
    const [roomInit, setRoomInit] = useState(false);

    const newRoom = () => {
        let room_ids = Rooms.map(room => room.id);
        setRoomID(room_ids.at(-1) + 1);
    }

    const addQuestion = (room_ID, question) => {
        console.log(roomID);

        console.log("TEST ADDING");
    }

    return(
        <>
            <Header />
            <div className="mt-64 justify-center items-center flex">

                <div className="h-64 w-80 bg-violet-500 rounded shadow-xl justify-center items-center flex">
                    <div className="flex-col">
                    {
                        roomInit
                        ? <HostGame
                            roomID={roomID}
                            addQuestion={addQuestion}
                        />
                        : <CreateRoom 
                            newRoom={newRoom}
                            setRoomInit={setRoomInit}
                        />
                    }
                    </div>
                </div>
            </div>

            <div>
                {
                    Rooms.map( room => {
                        return(
                            <div className="box">
                                { room.id } <br/>
                                {
                                    room.questions.map(q => {
                                        return(
                                            q
                                        );
                                    })
                                }
                            </div>
                        )
                    } )
                }
            </div>

            <Navbar />
        </>
    );
}

export default Host;
