import React, { useState, useEffect } from "react";
import Header from "../Base/Header";
import Navbar from "../Base/Navbar";
import { db } from "../config/firebase";
import { getDocs, collection, addDoc } from "firebase/firestore";

const Howto = () => {

    constÂ [ questions, setQuestions ] = useState([]);

    // NEW QUESTIONS STATE
    const [ gameid, setGameid ] = useState(0);
    const [ newQuestion, setNewQuestion ] = useState("");

    const questionsCollectionRef = collection(db, "questions");

    useEffect(() => {
        const getQuestions = async () => {
            // Les data fra firebase
            // Legg inn data i state
            try {
                const data = await getDocs(questionsCollectionRef);
                const filteredData = data.docs.map(doc => ({...doc.data()}));

                setQuestions(filteredData);
                console.log(filteredData);
            } catch (error) {
                console.log("Error: " + error);
            }
        };

        getQuestions();
    }, []);

    const onSubmitQuestion = async () => {
        try {
            await addDoc(questionsCollectionRef, {gameid: gameid, question: newQuestion});
        } catch(err) {
            console.log(err);
        }
    }

    return(
        <>
            <Header />

            <p className="justify-center items-center flex mt-32">How to</p>
            <div className="mt-8 flex items-center justify-center">
                <button className="bg-blue-500 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:bg-blue-700 rounded-xl">
                    Button
                </button>
            </div>
            <div>
                <input placeholder="game id" type="number" onChange={e => setGameid(Number(e.target.value))}/>
                <input placeholder="question" onChange={e => setNewQuestion(e.target.value)}/>
                <button onClick={onSubmitQuestion}></button>
            </div>
            <div>
                {
                    questions.map(question =>(
                        <div key={question.id}>
                            <p key="2">{question.question}</p>
                            <p>{question.id}</p>
                        </div>
                    ))
                }
            </div>
            <Navbar />
        </>
    );
}

export default Howto;
