import React, { useState } from "react";
import Navbar from "../Base/Navbar";
import Header from "../Base/Header";
import { Link } from "react-router-dom";

/*
    Side hvor man får en id andre kan joine med
    kan legge til spørsmål selv
    kan starte spillet når som helst
    har count på antall spørsmål
*/

/*
    TODO - later
    fikse regex lengde på input
*/
const Host = (props) => {

    const [roomID, setRoomID] = useState(0);
    const [roomInit, setRoomInit] = useState(false);

    const newRoom = () => {
        const roomID = generateRoomNumber();
        setRoomID(Math.floor(Math.random() * 10 + 1));
        console.log("Creating room");
    }

    function generateRoomNumber() {
        console.log("Generating room number ...");
        let number = Math.floor(Math.random() * 9000 + 1000);

        return number;
    }

    const addQuestion = (question) => {

    }

    return(
        <>
            <Header />
            <div className="mt-64 justify-center items-center flex">

                <div className="h-64 w-80 bg-violet-500 rounded shadow-xl justify-center items-center flex">
                    <div className="flex-col">
                    {
                        roomInit
                        ? <HostGame roomID={roomID}/>
                        : <CreateRoom 
                            newRoom={newRoom}
                            setRoomInit={setRoomInit}
                            addQuestion={addQuestion}
                        />
                    }
                    </div>
                </div>
            </div>

            <Navbar />
        </>
    );
}

const CreateRoom = ({newRoom, setRoomInit}) => {

    const handleClick = () => {
        setRoomInit(true);
        newRoom();
    }

    return(
        <>
            <h1 className="justify-center items-center flex font-bold text-white">Your name</h1>
            <input id="hostName" name="id" className=" h-10 w-64 rounded shadow-md m-1" />

            <button onClick={handleClick} id="hostBtn" className="m-1 h-10 w-64 bg-blue-500 rounded shadow-md justify-center items-center flex hover:bg-blue-800">
            <p>Create room</p>
            </button>
        </>
    );
}

const HostGame = ({roomID}) => {
    return(
        <>
            <h1 className="justify-center items-center flex font-bold text-white">Room ID: {roomID}</h1>
            <input id="hostName" name="id" className=" h-10 w-64 rounded shadow-md m-1" />

            <button id="hostBtn" className="m-1 h-10 w-64 bg-blue-500 rounded shadow-md justify-center items-center flex hover:bg-blue-800">
            <p>Create room</p>
            </button>
        </>
    );
}

export default Host;
