import React, { useEffect } from "react";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";


const SnapPage: React.FC = () => {

  const control = useAnimation();
  const [ref, inView] = useInView();

  const boxVariant = {
    visible: { opacity: 1, scale: 2 },
    hidden: { opacity: 0, scale: 0 },
  }

  return (
      <>
        <div className="snap-y snap-mandatory h-screen overflow-scroll transition-all duration-500">
          <div className="snap-start bg-amber-200 w-screen h-screen flex items-center justify-center text-8xl">
            <motion.div
              ref={ref}
              className="w-32 h-32 bg-violet-400"
              variants={boxVariant}
              initial="hidden"
              animate="visible"
            >1</motion.div>
          </div>
          <div className="snap-start bg-teal-200 w-screen  h-screen flex items-center justify-center text-8xl">
            <motion.div
              ref={ref}
              className="w-32 h-32 bg-violet-400"
              variants={boxVariant}
              initial="hidden"
              animate="visible"
            >2</motion.div>
          </div>
          <div className="snap-start bg-cyan-200 w-screen h-screen flex items-center justify-center text-8xl">
            <motion.div className="w-32 h-32 bg-violet-400">3</motion.div>
          </div>
          <div className="snap-start bg-fuchsia-200 w-screen h-screen flex items-center justify-center text-8xl">
            <motion.div className="w-32 h-32 bg-violet-400">4</motion.div>
          </div>
        </div>
      </>
  );
}

export default SnapPage;
